mapper.select:
SELECT COUNT(pid) FROM t_paper
WHERE 1=1 AND display=1
and course=#{0}
and grade=#{1}
and grade=#{0} and course=#{1}

SELECT pId,title FROM t_paper
WHERE 1=1 AND display=1
 ORDER BY sort desc, pubTime desc limit #{2},#{3}
and course=#{1} ORDER BY sort desc, pubTime desc limit
	#{2},#{3}

and grade=#{0} ORDER BY sort desc, pubTime desc limit
	#{2},#{3}

and grade=#{0} and course=#{1} ORDER BY sort desc, pubTime
	desc limit #{2},#{3}

		SELECT pId,title FROM t_paper
		WHERE 1=1 AND display=1

		 ORDER BY sort desc, pubTime desc limit #{3},#{4}
		and degree=#{0} ORDER BY sort desc, pubTime desc limit
			#{3},#{4}

		
		and grade=#{1} ORDER BY sort desc, pubTime desc limit
			#{3},#{4}

		
		and course=#{2} ORDER BY sort desc, pubTime desc limit
			#{3},#{4}

		
		and degree=#{0} and grade=#{1} ORDER BY sort desc, pubTime
			desc limit #{3},#{4}

		
		and degree=#{0} and course=#{2} ORDER BY sort desc,
			pubTime desc limit #{3},#{4}

		
		and grade=#{1} and course=#{2} ORDER BY sort desc, pubTime
			desc limit #{3},#{4}

		
		and degree=#{0} and grade=#{1} and course=#{2} ORDER BY
			sort desc, pubTime desc limit #{3},#{4}
